let tray = msg.trayConfig.tray;
let mqttMsg = null;
let normalSettingMsg = {topic:'settingDone', payload:tray};
let trayControlMessage = null;
let trayProps  = flow.get('trayProps');

let icube = msg.trayConfig.blinkybusSettingCubes.indexOf(msg.payload.cube);

if (icube >= 0)
{
    let cube = msg.trayConfig.tray[msg.payload.cube];
    cube.value = msg.payload.value;
    let bufsize = 4;
    let buffer = new ArrayBuffer(bufsize);
    let settingsView = new DataView(buffer);
    settingsView.setUint8(0, 1); //setting
    settingsView.setUint8(1, cube.blinkybus.address); //address
    settingsView.setInt16(2, Math.round(cube.value * cube.blinkybus.scale), true); //value
    mqttMsg = {topic: trayProps.box + "/" + msg.trayConfig.cubeType + "/" + tray.type + "/" + tray.name + "/setting", payload:Buffer.from(buffer)};
}
else
{
    switch (msg.payload.cube) 
    {
        case 'alias':
            tray.alias.value = msg.payload.value;
        break;
        case 'trayControl':
            switch(msg.payload.value)
            {
                case 0:
                    tray.trayControl.value = 0;
                    break;
                case 1:
                    tray.trayControl.value = -1;
                    break;
                case 2:
                    if (tray.trayControl.value > 0)
                    {
                        tray.trayControl.value = 2;
                        trayControlMessage =
                        {
                            topic: trayProps.box + '/tray/' + tray.type + '/' + tray.name + '/echo',
                            payload: JSON.stringify({ topic: trayProps.box + '/tray/' + tray.type + '/' + tray.name + '/setting/echo' })
                        };
                    }
                    break;
                default:
                    tray.trayControl.value = 0;
                    break;
             }
        break;
        default:
            break;
    }
}
return [mqttMsg, normalSettingMsg, trayControlMessage];

let cubeAlarm = msg.trayConfig.tray[msg.payload.cube].alarm;
cubeAlarm.limits.lolo = msg.payload.limits.lolo;
cubeAlarm.limits.low  = msg.payload.limits.low;
cubeAlarm.limits.high = msg.payload.limits.high;
cubeAlarm.limits.hihi = msg.payload.limits.hihi;
cubeAlarm.notify.name = msg.payload.notify.name;
cubeAlarm.notify.number = msg.payload.notify.number;
return {topic:'trayConfigCompleted', payload:msg.trayConfig.tray};
